build.script:
#!/bin/sh
open -a docker && while ! docker info > /dev/null 2>&1; do sleep 1 ; done
docker run --rm -v $(pwd):/build devkitpro/devkitarm make -C /build

clean.script:
#!/bin/sh
open -a docker && while ! docker info > /dev/null 2>&1; do sleep 1 ; done
docker run --rm -v $(pwd):/build devkitpro/devkitarm make clean -C /build
